    avi_indicator.smoothToShow();        //Creating an object of our api interface        RestApiInterface apiInterface = APIClient.getClient().create(RestApiInterface.class);        Call<SPServiceListResponse> call = apiInterface.SPServiceListResponseCall(RestUtils.getContentType());        Log.w(TAG,"url  :%s"+ call.request().url().toString());        call.enqueue(new Callback<SPServiceListResponse>() {            @SuppressLint({"SetTextI18n", "LogNotTimber"})            @Override            public void onResponse(@NonNull Call<SPServiceListResponse> call, @NonNull Response<SPServiceListResponse> response) {                avi_indicator.smoothToHide();                if (response.body() != null) {                    if(200 == response.body().getCode()){                        Log.w(TAG,"SPServiceListResponse" + new Gson().toJson(response.body()));                        if(response.body().getData() != null) {                            if(response.body().getData().getService_list() != null) {                                spServiceList = response.body().getData().getService_list();                            }                            Log.w(TAG,"SPServiceListResponse" + new Gson().toJson(response.body()));                            if(response.body().getData().getSpecialization() != null) {                                spSpecialzationList = response.body().getData().getSpecialization();                            }                            if(response.body().getData().getTime() != null) {                                spTimeList = response.body().getData().getTime();                            }                            if(spTimeList != null && spTimeList.size()>0) {                                for (int i = 0; i < spTimeList.size(); i++) {                                    strTimeslot = spTimeList.get(0).getTime();                                }                                if(bus_service_list != null) {                                    for (int j = 0; j < bus_service_list.size(); j++) {                                        bus_service_list.get(j).setTime_slots(strTimeslot);                                    }                                }                            }                           Log.w(TAG,"bus_service_list : "+new Gson().toJson(bus_service_list));                            Log.w(TAG,"spSpecialzationListEdit : "+new Gson().toJson(spSpecialzationListEdit));                            if(spSpecialzationListEdit != null && spSpecialzationList != null) {                                for (int i = 0; i < spSpecialzationListEdit.size(); i++) {                                    for (int j = 0; j < spSpecialzationList.size(); j++) {                                        if (spSpecialzationListEdit.get(i).getBus_spec_list().equalsIgnoreCase(spSpecialzationList.get(j).getSpecialization())) {                                            ServiceProviderRegisterFormCreateRequest.BusSpecListBean busSpecListBean = new ServiceProviderRegisterFormCreateRequest.BusSpecListBean();                                            busSpecListBean.setBus_spec_list(spSpecialzationList.get(j).getSpecialization());                                            bus_spec_list.add(busSpecListBean);                                        }                                    }                                }                            }                            Log.w(TAG,"bus_spec_list : "+new Gson().toJson(bus_spec_list));                        }                        if(spServiceListEdit != null && spServiceListEdit.size()>0 && spServiceList != null && spServiceList.size()>0){                            for(int i=0;i<spServiceListEdit.size();i++){                                for(int j=0;j<spServiceList.size();j++) {                                    if (spServiceListEdit.get(i).getBus_service_list().equalsIgnoreCase(spServiceList.get(j).getService_list())) {                                        spServiceList.get(j).setChbxChecked(true);                                        spServiceList.get(j).setValueAdded(true);                                        spServiceList.get(j).setTime_slots(spServiceListEdit.get(i).getTime_slots());                                        spServiceList.get(j).setAmount(spServiceListEdit.get(i).getAmount());                                        ServiceProviderRegisterFormCreateRequest.BusServiceListBean busServiceListBean = new ServiceProviderRegisterFormCreateRequest.BusServiceListBean();                                        busServiceListBean.setBus_service_list(spServiceListEdit.get(j).getBus_service_list());                                        busServiceListBean.setTime_slots(spServiceListEdit.get(j).getTime_slots());                                        busServiceListBean.setAmount(spServiceListEdit.get(j).getAmount());                                        bus_service_list.add(busServiceListBean);                                    }                                }                        }                        }                        Log.w(TAG,"spServiceList : "+new Gson().toJson(spServiceList));                        if(spServiceList != null && spServiceList.size()>0){                            rcylr_sp_servicelist.setVisibility(View.VISIBLE);                            txt_no_records.setVisibility(View.GONE);                            setSPServiceList(spServiceList,spServiceListEdit);                        }else{                            rcylr_sp_servicelist.setVisibility(View.GONE);                            txt_no_records.setVisibility(View.VISIBLE);                            txt_no_records.setText("No services found");                        }                        Log.w(TAG,"spSpecialzationList : "+new Gson().toJson(spSpecialzationList));                        if(spSpecialzationList != null && spS