og.setContentView(R.layout.alert_add_sp_amout_layout);            dialog.setCancelable(false);            Button btn_add = dialog.findViewById(R.id.btn_add);            Spinner spr_timeslottype = dialog.findViewById(R.id.spr_timeslottype);            ImageView img_close = dialog.findViewById(R.id.img_close);            EditText edt_amount = dialog.findViewById(R.id.edt_amount);            edt_amount.setTransformationMethod(new NumericKeyBoardTransformationMethod());            ArrayList<String> timetypeArrayList = new ArrayList<>();            //timetypeArrayList.add("Select Time Type");            if(spTimeList != null && spTimeList.size()>0){                for (int i = 0; i < spTimeList.size(); i++) {                    String timeType = spTimeList.get(i).getTime();                    timetypeArrayList.add(timeType);                    ArrayAdapter<String> spinnerArrayAdapter = new ArrayAdapter<>(ServiceProviderEditFormActivity.this, R.layout.spinner_item, timetypeArrayList);                    spinnerArrayAdapter.setDropDownViewResource(R.layout.spinner_item); // The drop down view                    spr_timeslottype.setAdapter(spinnerArrayAdapter);                }            }            spr_timeslottype.setOnItemSelectedListener(new AdapterView.OnItemSelectedListener() {                @SuppressLint("LongLogTag")                @Override                public void onItemSelected(AdapterView<?> parent, View view, int arg2, long arg3) {                    String  strTimeslot = spr_timeslottype.getSelectedItem().toString();                    Log.w(TAG,"strTimeslot : "+strTimeslot);                    spServiceList.get(position).setTime_slots(strTimeslot);                }                @Override                public void onNothingSelected(AdapterView<?> arg0) {                    // TODO Auto-generated method stub                }            });            btn_add.setOnClickListener(new View.OnClickListener() {                @SuppressLint("LongLogTag")                @Override                public void onClick(View view) {                    if(edt_amount.getText().toString().isEmpty()){                        Toasty.warning(getApplicationContext(),"Please enter the service amount",Toast.LENGTH_SHORT).show();                    }else{                        spServiceList.get(position).setAmount(Integer.parseInt(edt_amount.getText().toString()));                        spServiceList.get(position).setChbxChecked(true);                        spServiceList.get(position).setValueAdded(true);                        // bus_service_list.get(position).setAmount(Integer.valueOf(edt_amount.getText().toString()));                        spServiceListEditAdapter.notifyItemChanged(position);                        ServiceProviderRegisterFormCreateRequest.BusServiceListBean busServiceListBean = new ServiceProviderRegisterFormCreateRequest.BusServiceListBean();                        busServiceListBean.setBus_service_list(spServiceList.get(position).getService_list());                        busServiceListBean.setTime_slots(spServiceList.get(position).getTime_slots());                        busServiceListBean.setAmount(spServiceList.get(position).getAmount());                        bus_service_list.add(busServiceListBean);                        Log.w(TAG + " onItemSPServiceCheck added_service_list",new Gson().toJson(bus_service_list));                    }                    dialog.dismiss();                }            });            img_close.setOnClickListener(new View.OnClickListener() {                @Override                public void onClick(View v) {                    Log.w(TAG,"img_close : "+"position : "+position);                    if(spServiceList != null && spServiceList.size()>0){                        spServiceList.get(position).setValueAdded(false);                        spServiceList.get(position).setChbxChecked(false);                        spServiceListEditAdapter.notifyItemChanged(position);                        Log.w(TAG,"img_close : "+"notifyItemChanged position : "+position);                    }                    Log.w(TAG,"img_close spServiceList : "+new Gson().toJson(spServiceList));                    dialog.dismiss();                }            });            Objects.requireNonNull(dialog.getWindow()).setBackgroundDrawable(new ColorDrawable(Color.TRANSPARENT));            dialog.show();        } catch (WindowManager.BadTokenException e) {            e.printStackTrace();        }    }}